head.script:
//    
    var svgDoc, Ox, Oy, DragPoint = null;//// ref="globalsS"
    var Pos = [], P = [], Reg = [], PVal = [];
    var IPos =[[250,400,"red"],[530,400,"lime"],//// ref="controlPoints1"
               [410,130,"green"],[250,240,"orange"]];//// ref="controlPoints2"
    var NumPos = IPos.length;
    var paths =[["fill","#DDD", "stroke","none","opacity","0.8"],//// ref="fillInfoS"
                ["fill","#C22", "stroke","none","opacity","0.8"],
                ["fill","#3D3", "stroke","none","opacity","0.8"],//// ref="fillInfoE"
                ["stroke-width","2","fill","none","stroke","black"], //// ref="fillInfoLine"
                ["stroke-width","3","fill","none","stroke","blue"],  //// ref="fillInfoQ1"
                ["stroke-width","3","fill","none","stroke","yellow"],//// ref="fillInfoQ2"
                ["stroke-width","4","fill","none","stroke","black"]];//// ref="fillInfoP2"
    function init() {//// ref="initS"
      var i, j, k, TR, TD, TD2, TBL;
      svgDoc = document.getElementById("canvas");//// ref="canvas"
      MKSVGElm(svgDoc, "rect",                   //// ref="rectS"
        ["x", "0", "y", "0", "width", "700", "height", "500",
         "stroke-width", "3", "stroke", "black", "fill", "white"],[]);//// ref="rectE"
      for(i=0;i<paths.length; i++) {                        //// ref="auxLineS"
        Reg[i] = MKSVGElm(svgDoc, "path", paths[i],[]);     
      }                                                     //// ref="auxLineE"
      TBL = document.getElementById("Table");
      for(i=0; i<NumPos; i++) {                            //// ref="setControlPointsS"
        TR = MKHTMLElm(TBL, "tr",[],[]);                   //// ref="makeTable"
        TD = MKHTMLElm(TR, "td",["style", "color:"+ IPos[i][2],"align","center"],[]);//// ref="makeTR"
        TD.appendChild(document.createTextNode("P"+i));                          //// ref="makeText"
        PVal[i] = new BoxP(TR);                                                  //// ref="makeInput"
        Pos[i] = new Point(svgDoc, IPos[i][0], IPos[i][1], IPos[i][2], 6, //// ref="setControlPointsS"
          ["mousedown", beginDrag, false, "mouseup", endDrag, false]);    //// ref="setControlPointsE"
      }                                                   //// ref="setControlPointsE"
      for(k=(NumPos-1)*NumPos/2;k>0;k--,i++){             //// ref="setMidPointsS"
          Pos[i] = new Point(svgDoc, 0, 0, "black", "6", []);
      }                                                  //// ref="setMidPointsE"
      document.getElementById("Portion").value="1/2";    //// ref="setPortion"
      RefreshRegion();//// ref="refreshRegion"
    }//// ref="initE"
    function Point(P, cx, cy, fill, r,  Events) {//// ref="PointsS"
      this.Circle = MKSVGElm(P, "circle",        //// ref="makeCircleS"
         ["cx", cx, "cy", cy, "fill", fill, "r", r], Events);//// ref="makeCircleE"
      this.x = cx;//// ref="setCx"
      this.y = cy;//// ref="setCy"
    }                                            //// ref="PointsE"
    Point.prototype.setPos = function(PV) {
      SetAttributes(this.Circle,["cx",PV.x.value, "cy", PV.y.value]);//// ref="setCenter"
      this.x = parseInt(PV.x.value);//// ref="refreshX"
      this.y = parseInt(PV.y.value);//// ref="refreshY"
    }
    Point.prototype.movePos = function(xx, yy) {//// ref="movePosS"
      SetAttributes(this.Circle,["cx",xx, "cy", yy]);
      this.x = xx;
      this.y = yy;
    }//// ref="movePosE"
    Point.prototype.getPos = function() {//// ref="getPosS"
      this.x = parseFloat(this.Circle.getAttribute("cx"));
      this.y = parseFloat(this.Circle.getAttribute("cy"));
    }//// ref="getPosE"
    Point.prototype.toSt = function() {//// ref="toSt"
      return this.x+","+this.y+" ";
    }                                        //// ref="readCxCyE"
    function BoxP(P) {     //// ref="BoxPS"
      var O = MKHTMLElm(P, "td",[],[]);
      this.x = MKHTMLElm(O, "input", ["type", "text", "size","3"],[]);
      O = MKHTMLElm(P, "td",[],[]);
      this.y = MKHTMLElm(O, "input", ["type", "text", "size","3"],[]);
    }     //// ref="BoxPE"
    BoxP.prototype.setVal = function(Po) {//// ref="BoxPValS"
      Po.getPos();  //// ref="setTextBoxValfromObj1"
      this.x.value = Po.x;
      this.y.value = Po.y;
    }   //// ref="BoxPValE"
    function SetPos() {//// ref="setPosS"
      for(var i=0;i<NumPos;i++) Pos[i].setPos(PVal[i]);//// ref="setpos"
      RefreshRegionN();
    }/// ref="setPosE"
    function RefreshRegion(){//// ref="RefreshRegionS"
      for(var i=0;i<NumPos;i++) PVal[i].setVal(Pos[i]);//// ref="setTextBoxValfromObjs"
      RefreshRegionN();
    }//// ref="RefreshRegionE"
    function RefreshRegionN(){//// ref="RefreshRegionNS"
      var i, j=NumPos, k, d, db, A;
      A = eval(document.getElementById("Portion").value);//// ref="checkPS"
      if(A>=1 || A<=0) {
        alert("Please set A between 0 and 1");
        A=0.5;
        document.getElementById("Portion").value="1/2";
      }//// ref="checkPE"
      d  = "M"+Pos[0].toSt();//// ref="setInitialPathS"
      for(i=1, db = "";i<4;i++) db += Pos[i].toSt();//// ref="setInitialPathE"
      Reg[6].setAttribute("d",d+"C"+db);//// ref="drawBeziere"
      d += db;//// ref="auxLinesS"
      for(i=NumPos; i>1; i--) {
        d+= "M";
        for(k=1; k<i; k++, j++) {
          Pos[j].movePos(Pos[j-i].x*(1-A)+Pos[j-i+1].x*A, 
                         Pos[j-i].y*(1-A)+Pos[j-i+1].y*A);
          d += Pos[j].toSt();//alert(j);
        }
      }
      Reg[3].setAttribute("d",d);//// ref="auxLinesE"
      Reg[4].setAttribute("d",   //// ref="drawQBezierS"
            "M"+Pos[0].toSt()+"Q"+Pos[1].toSt()+Pos[2].toSt());
      Reg[5].setAttribute("d",
            "M"+Pos[1].toSt()+"Q"+Pos[2].toSt()+Pos[3].toSt());//// ref="auxLinesE"
      PaintRegion(0,Pos[0],Pos[1],Pos[2],Pos[3]);//// ref="showConvexregion1"
      PaintRegion(1,Pos[0],Pos[4],Pos[7],Pos[9]);
      PaintRegion(2,Pos[9],Pos[8],Pos[6],Pos[3]);//// ref="showConvexregion3"
    }//// ref="RefreshRegionNE"
    function PaintRegion(S, P0, P1, P2, P3){//// ref="PaintRegionS"
      var path, PSS=getSign(P0,P1,P2);
      if(getSign(P0,P1,P3) != PSS) {
        if(getSign(P1,P2,P3)==PSS) {
          path = P3.toSt()+P1.toSt()+P2.toSt();
          if(getSign(P2,P0,P3)==PSS) path += P0.toSt();
        } else {
          path = P3.toSt()+P2.toSt()+P0.toSt();
        } 
      } else {
        if(getSign(P1,P2,P3)==PSS) {
          path = P0.toSt()+P1.toSt()+P2.toSt();
          if(getSign(P2,P0,P3)!=PSS) path += P3.toSt();
        } else {
          path =P0.toSt()+P1.toSt()+P3.toSt();
          if(getSign(P2,P0,P3)==PSS) path += P2.toSt();
        } 
      }
      Reg[S].setAttribute("d", "M"+path+"z");
    }//// ref="PaintRegionE"
    function getSign(A,B,C){//// ref="getSignS"
      return((B.x-A.x)*(C.y-A.y)-(C.x-A.x)*(B.y-A.y)>=0)?1:-1;
    }//// ref="getSignE"
    function beginDrag(evt) {//// ref="mousedownS"
      if(!DragPoint) {
        DragPoint = evt.target;
        svgDoc.appendChild(DragPoint);//// ref=moveTargetUp"
        Ox = DragPoint.getAttribute("cx")-evt.clientX;//// ref="savePosx"
        Oy = DragPoint.getAttribute("cy")-evt.clientY;//// ref="savePosy"
        svgDoc.addEventListener("mousemove", Dragging, true);//// ref="addEventmousemove"
      }
    }//// ref="mousedownE"
    function Dragging(evt) {//// ref="mousemoveS"
      evt.stopPropagation();
      SetAttributes(DragPoint, ["cx", Ox+evt.clientX,"cy", Oy+evt.clientY]);
      RefreshRegion();
    }//// ref="mousemoveE"
    function endDrag(evt) {//// ref="mouseupS"
      svgDoc.removeEventListener("mousemove", Dragging, true);
      DragPoint = null;
    }//// ref="mouseupE"
//
